import React, { useState } from "react";

function CreateArea(props) {
    const [note, setNote] = useState({
        title:"",
        content:""
    });

    function handleChange(event) {
        const { name, value } = event.target;

        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            }
            
        });
       
        
    }
    
    function submitNote(event)
    {
        
        event.preventDefault();
      
        props.onAdd(note);
      
        this.text1.value = "";
        this.text2.value = "";
        
    }
   


    return (
        <div>
            <form>
                <input type="text1" name="title" onChange={handleChange} values={note.title} placeholder="title" ref={(el) => (this.text1 = el)} />
                <textarea type="text2" name="content" onChange={handleChange} values={note.content} placeholder="content" rows="3" ref={(el) => (this.text2 = el)}/>
                <button onClick={submitNote}>Add</button>
            </form>
        </div>
    )
}

export default CreateArea;
