import React, { useState } from "react";
import Header from "./Heading";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App(){
    const [notes, setNotes] = useState([]);

    function addNote(note)
    {
        setNotes((prevNote) => {
            return {
                ...prevNote,
                note
            }
        })
    }
    return(
    <div>
        <Header/>
        <CreateArea
            onAdd={addNote}
        />
        <Footer/>
        <Note/>
    </div>
   
       
    )
}

export default App;
